import React, { useState, useEffect } from 'react';

const Hero = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  
  const slides = [
    {
      title: "India's #1 Home Rooftop Solar Company",
      subtitle: "Save 90% on electricity bills with 0 EMI solar installation",
      image: "🏠"
    },
    {
      title: "UL-Certified Solar Systems",
      subtitle: "India's only UL-certified solar company with 25+ years warranty",
      image: "⚡"
    },
    {
      title: "Go Green, Go Solar",
      subtitle: "Join thousands of satisfied customers saving money with solar",
      image: "🌟"
    }
  ];

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % slides.length);
    }, 5000);
    
    return () => clearInterval(timer);
  }, [slides.length]);

  const goToSlide = (index) => {
    setCurrentSlide(index);
  };

  return (
    <section className="bg-gradient-to-br from-green-400 via-blue-500 to-purple-600 pt-16 min-h-screen flex items-center">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-20">
        <div className="text-center text-white">
          {/* Slide Content */}
          <div className="text-8xl mb-8 animate-pulse">
            {slides[currentSlide].image}
          </div>
          
          <h1 className="text-4xl md:text-6xl font-bold mb-6 leading-tight">
            {slides[currentSlide].title}
          </h1>
          
          <p className="text-xl md:text-2xl mb-8 text-blue-100 max-w-4xl mx-auto">
            {slides[currentSlide].subtitle}
          </p>

          {/* Call to Action Buttons */}
          <div className="flex flex-col sm:flex-row gap-4 justify-center mb-8">
            <button className="bg-white text-green-600 px-8 py-4 rounded-lg font-semibold hover:bg-gray-100 transition-all duration-300 transform hover:scale-105 shadow-lg">
              Get Free Consultation
            </button>
            <button className="border-2 border-white text-white px-8 py-4 rounded-lg font-semibold hover:bg-white hover:text-green-600 transition-all duration-300 transform hover:scale-105">
              Calculate Savings
            </button>
          </div>

          {/* Slide Indicators */}
          <div className="flex justify-center space-x-3">
            {slides.map((_, index) => (
              <button
                key={index}
                onClick={() => goToSlide(index)}
                className={`w-4 h-4 rounded-full transition-all duration-300 ${
                  currentSlide === index 
                    ? 'bg-white scale-110' 
                    : 'bg-white/50 hover:bg-white/75'
                }`}
                aria-label={`Go to slide ${index + 1}`}
              />
            ))}
          </div>

          {/* Stats Preview */}
          <div className="grid grid-cols-2 md:grid-cols-2 gap-4 mt-16 max-w-4xl mx-auto">
            <div className="bg-white/10 backdrop-blur-sm rounded-lg p-4">
              <div className="text-sm text-blue-100">Upto</div>
              <div className="text-2xl font-bold">90%</div>
              <div className="text-sm text-blue-100">Bill Reduction</div>
            </div>
            <div className="bg-white/10 backdrop-blur-sm rounded-lg p-4">
              <div className="text-2xl font-bold">12+</div>
              <div className="text-sm text-blue-100">Years Experience</div>
              <div className="text-sm text-blue-100">Engineers</div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Hero;
