import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const Hero = () => {
  const navigate = useNavigate();
  const [currentSlide, setCurrentSlide] = useState(0);

  const slides = [
    {
      title: "#1 Home Rooftop Solar Company",
      subtitle: "Save upto 90% on electricity bills with solar installation",
      image: "🏠"
    },
    {
      title: "Go Green, Go Solar",
      subtitle: "Join thousands of satisfied human saving money with solar",
      image: "🌟"
    }
  ];

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % slides.length);
    }, 5000);
    return () => clearInterval(timer);
  }, [slides.length]);

  const goToSlide = (index) => setCurrentSlide(index);

  return (
    <section className="bg-gradient-to-br from-green-400 via-blue-500 to-purple-600 pt-16 min-h-screen flex items-center">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-20">
        <div className="text-center text-white">
          {/* Fixed-width slide container */}
          <div className="relative m-1000">
            {/* Responsive fixed height so slides don't change container size
                Adjust h-64 / md:h-96 to suit your design */}
            <div className="relative h-100 md:h-96">
              {slides.map((slide, index) => (
                <div
                  key={index}
                  className={`absolute inset-0 flex flex-col items-center justify-center px-4 text-center transition-opacity duration-700 ease-in-out ${
                    index === currentSlide ? 'opacity-100' : 'opacity-0 pointer-events-none'
                  }`}
                  aria-hidden={index === currentSlide ? 'false' : 'true'}
                >
                  {/* Emoji / image (fixed-size box so width is stable) */}
                  <div className="flex items-center justify-center w-24 h-24 text-8xl mb-6">
                    {slide.image}
                  </div>

                  {/* Title (constrained to same max width as container) */}
                  <h1 className="text-3xl md:text-5xl font-bold mb-4 leading-tight max-w-full">
                    {slide.title}
                  </h1>

                  {/* Subtitle (already constrained) */}
                  <p className="text-lg md:text-xl mb-6 text-blue-100 max-w-2xl mx-auto">
                    {slide.subtitle}
                  </p>

                  {/* CTA Buttons */}
                  <div className="flex flex-col sm:flex-row gap-4 justify-center mb-4">
                    <button
                      onClick={() => navigate('/contact')}
                      className="bg-white text-green-600 px-8 py-3 rounded-lg font-semibold hover:bg-gray-100 transition-all duration-300 transform hover:scale-105 shadow-lg"
                    >
                      Get Free Consultation
                    </button>
                  </div>
                </div>
              ))}
            </div>

            {/* Slide Indicators (outside absolute area) */}
            <div className="flex justify-center space-x-3 mt-6">
              {slides.map((_, i) => (
                <button
                  key={i}
                  onClick={() => goToSlide(i)}
                  className={`w-4 h-4 rounded-full transition-all duration-300 ${
                    currentSlide === i ? 'bg-white scale-110' : 'bg-white/50 hover:bg-white/75'
                  }`}
                  aria-label={`Go to slide ${i + 1}`}
                />
              ))}
            </div>

            {/* Stats Preview (kept outside absolute slides so they don't reflow) */}
            <div className="grid grid-cols-2 md:grid-cols-2 gap-4 mt-8 max-w-4xl mx-auto">
              <div className="bg-white/10 backdrop-blur-sm rounded-lg p-4">
                <div className="text-sm text-blue-100">Upto</div>
                <div className="text-2xl font-bold">90%</div>
                <div className="text-sm text-blue-100">Bill Reduction</div>
              </div>
              <div className="bg-white/10 backdrop-blur-sm rounded-lg p-4">
                <div className="text-2xl font-bold">12+</div>
                <div className="text-sm text-blue-100">Years Experience</div>
                <div className="text-sm text-blue-100">Engineers</div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Hero;
